corpus = [dictionary1.doc2bow(text.split()) for text in san_pham_df['mo_ta_phan_tich']]

tfidf_model = TfidfModel(corpus)
tfidf_corpus = tfidf_model[corpus]

similarity_index = Similarity(output_prefix='output', corpus=tfidf_corpus, num_features=len(dictionary1))

def recommend_products_gensim(sp_id, df, index, nums=5):
    # Lấy index sản phẩm & phần mô tả đã được xử lý
    target_product = df[df['ma_san_pham'] == sp_id].index[0]

    # Obtain the number of features based on dictionary:
    target_bow = dictionary1.doc2bow(df.loc[target_product, 'mo_ta_phan_tich'].split())
    target_tfidf = tfidf_model[target_bow]

    # similarity calculation
    gensim_score = similarity_index[target_tfidf]

    # Tạo cột cosine_sim trong DF
    df['gensim_score'] = gensim_score

    # Lọc kết quả dựa trên đánh giá từ 4 sao trở lên và giá bán
    recommend_filtered = df[(df['diem_trung_binh'] >= 4)]

    # Sắp xếp DF theo cột cosine_sim
    recommend_filtered = recommend_filtered.sort_values(by='gensim_score', ascending=False).head(nums)

    print(f"Product ID: {sp_id}, Name: {san_pham_df.loc[target_product, 'ten_san_pham']}, Rating: {san_pham_df.loc[target_product, 'diem_trung_binh']}")
    return recommend_filtered[['ma_san_pham', 'ten_san_pham', 'diem_trung_binh', 'gensim_score']]

print('GenSim Recommend')
