# Hàm đề xuất với Cosine Similarity
def recommend_products_cosine(sp_id, df, nums=5):
    # Vector hóa nội dung
    vectorizer = TfidfVectorizer(analyzer='word', stop_words=stop_words)
    tfidf_matrix = vectorizer.fit_transform(df['Content_wt'])

    # Lấy chỉ số sản phẩm dựa trên ID
    product_idx = san_pham_df.index[df['ma_san_pham'] == sp_id][0]

    # Tính toán độ tương đồng
    cosine_sim = cosine_similarity(tfidf_matrix[product_idx], tfidf_matrix).flatten()

    # Tạo cột cosine_sim trong DF
    df['cosine_sim_score'] = cosine_sim

    # Lọc kết quả dựa trên đánh giá từ 4 sao trở lên
    recommend_filtered = df[(df['diem_trung_binh'] >= 4)]

    # Sắp xếp DF theo cột cosine_sim
    recommend_filtered = recommend_filtered.sort_values(by='cosine_sim_score', ascending=False).head(nums)

    print(f"Product ID: {sp_id}, Name: {san_pham_df.loc[product_idx, 'ten_san_pham']}, Rating: {san_pham_df.loc[product_idx, 'diem_trung_binh']}")
    return recommend_filtered[['ma_san_pham', 'ten_san_pham', 'diem_trung_binh', 'cosine_sim_score']]

